data class StockData(
    val icon: Int,
    val longName:String,
    val shortName: String,
    val equity: String,
    val market: String,
    val value: Double,
    val closedPrice: Double,
    val change: Double,
    val changeIcon: Int
)

fun dummyStockData(): List<StockData> {
    val stockDataList = mutableListOf<StockData>()

    for (i in 1..100) {
        val icon = R.drawable.logo1 // Replace with the appropriate drawable resource
        val longName="AB Bank Limited"
        val shortName = "AB Bank"
        val equity = if (i % 2 == 0) "Bond" else "Loan"
        val market = if (i % 2 == 0) "CSE" else "DSE"
        val value = 100.0 + i
        val closedPrice = 90.0 + i
        val change = value - closedPrice
       val  changeIcon = if (change >= 0) R.drawable.baseline_add_24 else R.drawable.minus
        val stockData = StockData(icon,longName, shortName, equity, market, value, closedPrice, change, changeIcon)
        stockDataList.add(stockData)
    }

    return stockDataList
}
